	beq $24 $0 fim
	
	#jal sonic_prep
	jal doc_prep
	jal timer
	
	addi $9 $9 -4
	addi $24 $24 -1
	j npc_joaninha_laco_walk


fim:
  addi $2 $0 10
  syscall

